    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        String inputtext = jTextArea1.getText();
        jTextField1.setText("Text Length : " +inputtext.length());
        jTextField2.setText("Line Counts : "+ inputtext.split("\n",-1).length);
        ///   jTextField3.setText(" Tab Counts : "+(inputtext.split("\t",-1).length-1);
        int a = inputtext.split("\t" , - 1 ).length-1;
        jTextField3.setText("Tab Counts : " +a) ;
        a = inputtext.split("\s",-1).length-1;
        jTextField4.setText("Space Count : " + a);

        jTextField5.setText("Word Count: " +  wordCount(inputtext));
        
        
        jComboBox1.removeAllItems();
        char [] temp = jTextArea1.getText().toCharArray();
        
        for( int i = 0 ; i < temp.length ; i++ ){
            if( arr[temp[i]] == 0 ){
               
                jComboBox1.addItem(Character.toString(temp[i]));
                arr[temp[i]]++ ; 
            }
            else{
                arr[temp[i]]++ ; 
            }
        }
        
        
    }//GEN-LAST:event_jButton1ActionPerformed

