private int wordCount(String inputtext){
     String[] s = inputtext.split(" ");
            int cnt = s.length;
            for(int i=0; i< s.length; i++){
                if(s[i].isBlank()== true ){
                    cnt--;
                }
            }
            return cnt;
}

private int getWordCount(String inputtext , String word){
    int  cnt =0;
    String[] s = inputtext.split(" ");
    for(int i=0; i<s.length ; i++){
        if(s[i].equals(word) == true){
            cnt++;
        }
    }
    return cnt;
}
 int [] arr = new int[256] ; 

